 * @param {number} n
 * @return {void} Do not return anything, modify nums1 in-place instead.
 */
var merge = function(nums1, m, nums2, n) {
    nums1.splice(m);
    nums2.splice(n);
    var pp=0;
    for(var i=0;i<n;i++){
        for(var j=pp;j<m;j++){
            if(nums2[i]<=nums1[j]){
                if(j===0){
                    nums1.unshift(nums2[i]);
                    pp = j + 1;
                    m = m + 1;
                    break;
                }
                else{
                    nums1.splice(j,0,nums2[i]);
                    pp = j+1;
                    m = m + 1;
                    break;
                }
            }
        }
        if(j>=m){nums1.push(nums2[i])}
    }
};
~~~

