/**
 * @param {string} s
 * @return {number}
 */
var romanToInt = function(s) {
    var num = [];
    for(var i=0; i<s.length; i++){
        switch(s[i]){
            case "I":
                num.push(1);
                break;
            case "V":
                num.push(5);
                break;
            case "X":
               num.push(10);
                break;
            case "L":
                num.push(50);
                break;
            case "C":
               num.push(100);
                break;
            case "D":
                num.push(500);
                break;
            case "M":
                num.push(1000);
                break;
        }

    }
    if(num.length===0){return 0;}
    if(num.length===1){return num[0];}
    var sum = num[0];
    for(var j=1 ;j<num.length;j++){
        if(num[j]<=num[j-1]){
